	<span id="timer"></span><a href="./code.php" id="resend" style="display:none;text-decoration: none;font-family: Telstra Akkurat;">Resend Code.</a>
  
<script>
    // Set the expiration time in minutes
    const expirationTime = 5;

    // Calculate the target time
    const targetTime = new Date().getTime() + (expirationTime * 60 * 1000);

    // Get the timer and resend button elements
    const timerElement = document.getElementById("timer");
    const resendButton = document.getElementById("resend");

    // Update the timer every minute
    const intervalId = setInterval(() => {
      // Calculate the remaining time in minutes and seconds
      const remainingTime = Math.round((targetTime - new Date().getTime()) / 1000 / 60);
      const remainingSeconds = Math.round((targetTime - new Date().getTime()) / 1000) % 60;

      // Stop the timer when the time is up
      if (remainingTime <= 0 && remainingSeconds <= 0) {
        clearInterval(intervalId);
        timerElement.textContent = "Code expired.";
        resendButton.style.display = "inline";
      } else {
        // Display the remaining time
        if (remainingTime > 0) {
          timerElement.textContent = `Your code will expire in ${remainingTime} minute(s) and ${remainingSeconds} second(s).`;
        } else {
          timerElement.textContent = `Your code will expire in ${remainingSeconds} second(s).`;
        }
      }
    }, 1000);

    // Resend the code when the button is clicked
    resendButton.addEventListener("click", () => {
      // Hide the button and restart the timer
      resendButton.style.display = "none";
      clearInterval(intervalId);
      const newTargetTime = new Date().getTime() + (expirationTime * 60 * 1000);
      intervalId = setInterval(() => {
        const remainingTime = Math.round((newTargetTime - new Date().getTime()) / 1000 / 60);
        const remainingSeconds = Math.round((newTargetTime - new Date().getTime()) / 1000) % 60;
        if (remainingTime <= 0 && remainingSeconds <= 0) {
          clearInterval(intervalId);
          timerElement.textContent = "Code expired.";
          resendButton.style.display = "inline";
        } else {
          if (remainingTime > 0) {
            timerElement.textContent = `Your code will expire in ${remainingTime} minute(s) and ${remainingSeconds} second(s).`;
          } else {
            timerElement.textContent = `Your code will expire in ${remainingSeconds} second(s).`;
          }
        }
      }, 1000);
    });
  </script>
